import styled from "styled-components";
export const SectionTabsWrapper = styled.div`
  display: flex;
  margin:20px 0;
  .item {
    min-width: 120px;
    height: 48px;
    line-height: 48px;
    border: solid 0.5px #d8d8d8;
    border-radius: 3px;
    margin-right: 16px;
    text-align: center;
    cursor: pointer;
    font-size:17px;
    ${(props) => props.theme.mixin.boxShadow}
    &.active{
      background-color:${props=>props.theme.color.secondary};
    }
  }

`;
