import styled from 'styled-components'
export const ScrollViewWrapper=styled.div`
.tab-content {

  overflow: hidden;
  .scroll-content {
    position: relative;
    display: flex;


  }

}

.control{
  position:absolute;
  width: 28px;
    height: 28px;
    border-radius: 50%;
    text-align: center;
    border-width: 2px;
    border-style: solid;
    border-color: #fff;
    background: #fff;
    box-shadow: 0px 1px 1px 1px rgba(0,0,0,.14);
    cursor: pointer;
}
`
