import PropTypes from "prop-types";
import React, { memo, useEffect, useRef, useState } from "react";

import { SectionTabsWrapper } from "./style";
let classNames = require("classnames");
const SectionTabs = memo((props) => {
  const [showLeft, setShowLeft] = useState(false);
  const [showRight, setShowRight] = useState(false);
  const [currentPosIndex, setCurrentPosIndex] = useState(0);

  const { tabs, handleTabClick, activeName } = props;
  const wrapperRef = useRef();
  // 卷起来的长度
  const totalDistanceRef = useRef(0);
  // console.log(activeName)
  const leftHandleClick = () => {
    //左边按钮，当rightwidth+clientWidth<scrollWidth
  };

  const rightHandleClick = () => {
    // 右边按钮，1.先判断右边是否有值 2如果有，则index+1，并设置scrollLeft

    const scrollLeft = wrapperRef.current.children[currentPosIndex].offsetLeft;
    if (scrollLeft < totalDistanceRef.current) {
      setShowRight(true);
      setCurrentPosIndex(currentPosIndex + 1);
      wrapperRef.current.scrollLeft = wrapperRef.current.children[currentPosIndex+1].offsetLeft;
    } else {
      setShowRight(false);
    }
  };
  useEffect(() => {
    const scrollWidth = wrapperRef.current.scrollWidth;
    const clientWidth = wrapperRef.current.clientWidth;
    totalDistanceRef.current = scrollWidth - clientWidth;
    if (totalDistanceRef.current > 0) {
      setShowRight(true);
    } else {
      setShowRight(false);
    }
  }, []);
  return (
    <SectionTabsWrapper>
      {showLeft && <button onClick={leftHandleClick}>左边按钮</button>}
      {showRight && <button onClick={rightHandleClick}>右边按钮</button>}
      <div className="tab-content" ref={wrapperRef}>
        {tabs?.map((item, index) => (
          <div
            key={item.name}
            className={classNames("item", { active: activeName == item.name })}
            onClick={(e) => {
              handleTabClick(item.name);
              setCurrentPosIndex(index);
            }}
          >
            {item.name}
          </div>
        ))}
      </div>
    </SectionTabsWrapper>
  );
});

SectionTabs.propTypes = {
  tabs: PropTypes.array,
};

export default SectionTabs;
