import styled from "styled-components";

export const HeaderCenterWrapper = styled.div`
  .search-bar {
    width: 300px;
    height:48px;
    display: flex;
    justify-content:space-between;
    align-items:center;
    border:1px solid #ddd;
    ${props=>props.theme.mixin.boxShadow}
  }
`;
