import styled from "styled-components";

export const RoomItemWrapper = styled.div`
  width: 25%;
  padding: 8px 8px 12px;
  box-sizing: border-box;
  .cover {
    width: 100%;
    padding: 66.6% 0 0;
    position: relative;
    box-sizing: border-box;
    img {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      height: 100%;
    }
  }
  .desc {
    color: ${(props) => props.textColor};
    font-size: 12px;
  }
`;
